import React, {useState} from "react";
import {
    CardBody,
    CardTitle,
    CardSubtitle,
    Card,
    Table,
    Button, Modal, ModalHeader, ModalBody
} from "reactstrap";
import {useSelector} from "react-redux";
import moment from "moment";
import DayPicker from "react-day-picker";


//TODO Creation of a workout and listing of correct workout
const WorkoutWeekTable = ({workouts}) => {

    const currentWeek = useSelector(state => state.currentWeek)
    const [modal, setModal] = useState(false);

    const toggle = () => {
        setModal(!modal);
    }

    return (
        <div>
            {currentWeek.map(d => createDayCard(d, toggle, modal))}
        </div>
    )
};

const createDayCard = (date, toggle, modal) => {


    const day = moment(date).format('ddd');

    return (
        <Card>
            <CardTitle>{day}</CardTitle>
            <CardSubtitle>Push</CardSubtitle>
            <CardBody>
                <Table>
                    <thead>
                    <tr>
                        <th>Exercise</th>
                        <th>Reps</th>
                        <th>Sets</th>
                        <th>Achieved</th>
                    </tr>
                    </thead>
                    <tbody>
                    {/*workout content*/}
                    </tbody>
                </Table>
            </CardBody>
            <Button onClick={toggle}>Create Workout</Button>
            <Modal isOpen={modal} toggle={toggle}>
                <ModalHeader toggle={toggle}>Create a workout</ModalHeader>
                <ModalBody>
                </ModalBody>
            </Modal>
        </Card>
    )
};


export default WorkoutWeekTable;
